(12, '2023-07-16 02:54:23', 'ADV_BANNER_STATUS_CHANGE', 's1', 'Y', '#DEFAULT_EMAIL_FROM#', '#EMAIL_TO#', '[BID##ID#] #SITE_NAME#: Изменился статус баннера - [#STATUS#]', 'Статус баннера # #ID# изменился на [#STATUS#].\n\n>=================== Параметры баннера ===============================\n\nБаннер   - [#ID#] #NAME#\nКонтракт - [#CONTRACT_ID#] #CONTRACT_NAME#\nТип      - [#TYPE_SID#] #TYPE_NAME#\nГруппа   - #GROUP_SID#\n\n----------------------------------------------------------------------\n\nАктивность: #INDICATOR#\n\nПериод    - [#DATE_SHOW_FROM# - #DATE_SHOW_TO#]\nПоказан   - #SHOW_COUNT# / #MAX_SHOW_COUNT# [#DATE_LAST_SHOW#]\nКликнули  - #CLICK_COUNT# / #MAX_CLICK_COUNT# [#DATE_LAST_CLICK#]\nФлаг акт. - [#ACTIVE#]\nСтатус    - [#STATUS#]\nКомментарий:\n#STATUS_COMMENTS#\n----------------------------------------------------------------------\n\nИзображение - [#IMAGE_ALT#] #IMAGE_LINK#\nURL         - [#URL_TARGET#] #URL#\n\nКод: [#CODE_TYPE#]\n#CODE#\n\n>=====================================================================\n\nСоздан  - #CREATED_BY# [#DATE_CREATE#]\nИзменен - #MODIFIED_BY# [#DATE_MODIFY#]\n\nДля просмотра параметров баннера воспользуйтесь ссылкой:\nhttp://#SERVER_NAME#/bitrix/admin/adv_banner_edit.php?ID=#ID#&CONTRACT_ID=#CONTRACT_ID#&lang=ru\n\nПисьмо сгенерировано автоматически.\n', 'Статус баннера # <?=$arParams[\"ID\"];?> изменился на [<?=$arParams[\"STATUS\"];?>].\n\n>=================== Параметры баннера ===============================\n\nБаннер   - [<?=$arParams[\"ID\"];?>] <?=$arParams[\"NAME\"];?>\n\nКонтракт - [<?=$arParams[\"CONTRACT_ID\"];?>] <?=$arParams[\"CONTRACT_NAME\"];?>\n\nТип      - [<?=$arParams[\"TYPE_SID\"];?>] <?=$arParams[\"TYPE_NAME\"];?>\n\nГруппа   - <?=$arParams[\"GROUP_SID\"];?>\n\n\n----------------------------------------------------------------------\n\nАктивность: <?=$arParams[\"INDICATOR\"];?>\n\n\nПериод    - [<?=$arParams[\"DATE_SHOW_FROM\"];?> - <?=$arParams[\"DATE_SHOW_TO\"];?>]\nПоказан   - <?=$arParams[\